import React from 'react';
import PropTypes from 'prop-types';

import Card from '../Card';

export default function Board({ cards, flipped, handleClick }) {
  return (
    <div className='board'>
      {cards.map(card => (
        <Card
          id={1}
          width={100}
          height={100}
          back={'/img/back.png'}
          front={'/img/rm1.png'}
          flipped={flipped.includes(1)}
          handleClick={() => handleClick(1)}
        />
      ))}
    </div>
  );
}
